mapper.select:
select a.id,a.versionId,a.softwareName,a.APKName,a.softwareSize,a.status, (SELECT d.valueName FROM data_dictionary d where a.flatformId=d.valueId AND typeCode='APP_FLATFORM') AS flatformName, (SELECT c.categoryName FROM app_category c WHERE c.id=a.categoryLevel1) AS categoryLevel1Name, (SELECT c.categoryName FROM app_category c WHERE c.id=a.categoryLevel2) AS categoryLevel2Name, (SELECT c.categoryName FROM app_category c WHERE c.id=a.categoryLevel3) AS categoryLevel3Name, (SELECT d.valueName FROM data_dictionary d WHERE d.valueId=a.`status` AND typeCode='APP_STATUS') AS statusName, a.downloads, (SELECT v.versionNo FROM app_version v where v.id=a.versionId) AS versionNo FROM app_info a
and a.softwareName like CONCAT('%',#{softwareName},'%')
and a.`status`=#{status} and a.flatformId=#{flatformId} and a.categoryLevel1=#{categoryLevel1} and a.categoryLevel2=#{categoryLevel2} and a.categoryLevel3=#{categoryLevel3}
LIMIT #{pageIndex},#{pageConut}

select COUNT(1) FROM app_info

	and softwareName like CONCAT('%',#{softwareName},'%')
	
	and status=#{status} 
	and flatformId=#{flatformId}
	and categoryLevel1=#{categoryLevel1}
	and categoryLevel2=#{categoryLevel2}
	and categoryLevel3=#{categoryLevel3}

select * from app_info

   
    and APKName=#{APKName}
    
    
     and id=#{id}

	select a.*,(SELECT d.valueName FROM data_dictionary d where d.typeCode='APP_STATUS' AND d.valueId=a.`status`) AS statusName
    from app_info a where
	id=#{id}

mapper.update:
UPDATE app_info
softwareName=#{softwareName}, APKName=#{APKName}, supportROM=#{supportROM}, interfaceLanguage=#{interfaceLanguage}, softwareSize=#{softwareSize}, updateDate=#{updateDate}, devId=#{devId}, appInfo=#{appInfo}, status=#{status}, onSaleDate=#{onSaleDate}, offSaleDate=#{offSaleDate}, flatformId=#{flatformId}, categoryLevel3=#{categoryLevel3}, downloads=#{downloads}, modifyBy=#{modifyBy}, modifyDate=#{modifyDate}, categoryLevel1=#{categoryLevel1}, categoryLevel2=#{categoryLevel2},
where id=#{id}

mapper.select:
	  select a.id,v.*,(SELECT i.softwareName FROM app_info i where v.appId=i.id) AS appName,
     (SELECT d.valueName FROM data_dictionary d where v.publishStatus=d.valueId AND d.typeCode='PUBLISH_STATUS') AS publishStatusName
     from app_info a,app_version v where a.id=v.appId and a.id=#{id}
